import { StatusBar } from "expo-status-bar";
import {
  StyleSheet,
  Text,
  View,
  TextInput,
  TouchableOpacity,
} from "react-native";

export default function App() {
  return (
    <View style={styles.container}>
      <Text style={styles.title}>Login</Text>
      <Text style={styles.subtitle}>Sign in your account</Text>
      <TextInput
        style={styles.textInput}
        placeholder="jfquinterogu@cesde.net"
      />
      <TextInput style={styles.textInput} placeholder="****************" />
      <TouchableOpacity>
        <Text style={styles.text}>Forgot your password?</Text>
      </TouchableOpacity>
      <TouchableOpacity style={styles.button}>
        <Text>Sign In</Text>
      </TouchableOpacity>
      <TouchableOpacity>
        <Text style={styles.text}>Don't have an account?</Text>
      </TouchableOpacity>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#f1f1f1",
    alignItems: "center",
    justifyContent: "center",
  },
  title: {
    fontSize: 52,
    color: "#34434D",
    fontWeight: "bold",
  },
  subtitle: {
    margin: 12,
    fontSize: 18,
    color: "gray",
  },
  text: {
    margin: 15,
    fontSize: 14,
    color: "gray",
  },
  textInput: {
    textAlign: "center",
    padding: 10,
    width: "80%",
    height: 50,
    margin: 5,
    borderRadius: 30,
    backgroundColor: "#fff",
  },
  button: {
    textAlign: "center",
    borderWidth: 2,
    borderColor: "gray",
    borderRadius: 30,
    padding: 10,
    margin: 5,
    width: "40%",
  },
});
