import { URL } from "../../environments/env";
import axios from "axios";
export class UsersService {
  //#region service
  //getAll
  getAllUsers = async () => {
    return await axios.get(`${URL}/users/getAll`);
  };

  //byId
  getUserById = async (id) => {
    return await axios.get(`${URL}/users/getByEmail/${id}`);
  };

  //byEmail
  getUserByEmail = async (formData) => {
    return await axios.get(`${URL}/users/getByEmail/${formData.email}`);
  };

  createUser = async (user) => {
    return await axios.post(`${URL}/users/create`, user);
  };

  updateUser = async (user) => {
    return await axios.put(`${URL}/users/create`, user);
  };

  deleteUser = async (id) => {
    return await axios.delete(`${URL}/users/create/${id}`);
  };

  //#endregion
}
