import { Text, View, TouchableOpacity } from "react-native";
import { styles } from "../../style/style";
import React from "react";
// service
import { NavigationService } from "../../service/NavigationService";
import AsyncStorage from "@react-native-async-storage/async-storage";
import { Users } from "../../models/users";
// models

// localstorage
const USERS_INFO = "@userInfo";

export default function Menu({ navigation }) {
  let user = new Users();
  let users = [];
  const navigationService = new NavigationService();

  const getUsersData = async () => {
    try {
      const value = await AsyncStorage.getItem(USERS_INFO);
      if (value !== null) {
        users = JSON.parse(value);
        if (users !== []) {
          user = users[0];
          console.log(user);
        }
      }
    } catch (e) {
      // Error retrieving data
      console.log(e);
    }
  };

  getUsersData();

  return (
    <View style={styles.container}>
      <Text style={styles.title}>Bienvenido</Text>
      <Text style={styles.text}>Selecciona una opci√≥n</Text>
      <TouchableOpacity
        style={styles.button}
        onPress={() => {
          navigationService.navigateProductsForm({ navigation });
        }}
      >
        <Text>Products</Text>
      </TouchableOpacity>
      <TouchableOpacity
        style={styles.button}
        onPress={() => {
          navigationService.navigateSaleForm({ navigation });
        }}
      >
        <Text>Sale</Text>
      </TouchableOpacity>
      <TouchableOpacity
        style={styles.button}
        onPress={() => {
          navigationService.navigateUsersList({ navigation });
        }}
      >
        <Text>Users</Text>
      </TouchableOpacity>
      <TouchableOpacity
        style={styles.subtitle}
        onPress={() => {
          navigationService.logout({ navigation });
        }}
      >
        <Text>Cerrar Sesion</Text>
      </TouchableOpacity>
    </View>
  );
}
