import { Text, View, TextInput, TouchableOpacity } from "react-native";
import { useForm } from "react-hook-form";
import { Products } from "../../models/products";
import { URL } from "../../environments/env";
import { styles } from "../../style/style";
import { userAuthInfo } from "../../service/UserAuth";
import { Users } from "../../models/users";
import { useState, useEffect } from "react";
import React from "react";
import axios from "axios";
import AsyncStorage from "@react-native-async-storage/async-storage";

export default function SignIn({ navigation }) {
  //#region atributos
  const [formData, setFormData] = useState(new Users());
  const [userAuth, setUserAuth] = useState(new Users());
  const [errorMess, setErrorMess] = useState("");

  const { handleSubmit, reset } = useForm({
    defaultValues: new Users(),
  });

  //#endregion

  //#region functions

  //#region services
  //get one by email for login
  const getByEmail = async () => {
    if (formData.email !== "" && formData.password !== "") {
      await axios
        .get(`${URL}/users/getByEmail/${formData.email}`)
        .then(async (response) => {
          user = response.data;
          if (user.password === formData.password) {
            await AsyncStorage.setItem(USER_INFO, user);
            await AsyncStorage.setItem(AUTH_INFO, true);
            setErrorMess("Iniciando sesion...");
            setTimeout(() => {
              navigation.navigate("ProductsForm", {
                user: user,
                product: product,
                auth: auth,
              });
              setErrorMess("");
            }, 1500);
          } else {
            setErrorMess("El usuario no esta registrado");
            setTimeout(() => {
              navigation.navigate("Signup");
              setErrorMess("");
            }, 1500);
          }
        })
        .catch((e) => {
          setErrorMess(e);
          console.log(e);
          setTimeout(() => {
            setErrorMess("");
          }, 1500);
        });
    } else {
      setErrorMess("Todos los campos son obligatorios.");
      setTimeout(() => {
        setErrorMess("");
      }, 2000);
    }
    reset();
  };
  //#endregion

  //#region events
  const onChange = (e, type) => {
    setFormData({ ...formData, [type]: e });
  };
  //#endregion

  //#endregion

  let info = userAuthInfo();
  console.log(info);

  if (!info) {
    AsyncStorage.clear();
    navigation.navigate("Signin", { user: user });
  } else {
    return (
      <View style={styles.container}>
        <Text style={styles.title}>Sign In</Text>
        <Text style={styles.subtitle}>Login to the application</Text>
        <TextInput
          style={styles.textInput}
          placeholder="Email"
          onChangeText={(e) => onChange(e, "email")}
          defaultValue={formData.email}
        />
        <TextInput
          style={styles.textInput}
          placeholder="Password"
          secureTextEntry={true}
          onChangeText={(e) => onChange(e, "password")}
          defaultValue={formData.password}
        />
        <TouchableOpacity
          style={styles.button}
          onPress={handleSubmit(getByEmail)}
        >
          <Text>Sign In</Text>
        </TouchableOpacity>
        <TouchableOpacity
          onPress={() => {
            navigation.navigate("Signup", { user: user });
          }}
        >
          <Text style={styles.text}>Don't have an account?</Text>
        </TouchableOpacity>
        <Text style={{ fontWeight: "bold", marginTop: 10, color: "black" }}>
          {errorMess}
        </Text>
      </View>
    );
  }
}
