import React from "react";
import {
  StyleSheet,
  Text,
  View,
  TextInput,
  TouchableOpacity,
} from "react-native";

export default function SignIn({ navigation }) {
  return (
    <View style={styles.container}>
      <Text style={styles.title}>Sign In</Text>
      <Text style={styles.subtitle}>Login to the application</Text>
      {/* <TextInput style={styles.textInput} placeholder="Email" /> */}
      <TextInput style={styles.textInput} placeholder="Username" />
      <TextInput style={styles.textInput} placeholder="Password" />
      <TouchableOpacity
        style={styles.button}
        onPress={() => {
          console.log("Inicio de sesion...");
        }}
      >
        <Text>Sign In</Text>
      </TouchableOpacity>
      <TouchableOpacity
        onPress={() => {
          navigation.navigate("Products");
        }}
      >
        <Text style={styles.text}>Don't have an account?</Text>
      </TouchableOpacity>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#f1f1f1",
    alignItems: "center",
    justifyContent: "center",
  },
  title: {
    fontSize: 52,
    color: "#34434D",
    fontWeight: "bold",
  },
  subtitle: {
    margin: 12,
    fontSize: 18,
    color: "gray",
  },
  text: {
    margin: 15,
    fontSize: 14,
    color: "gray",
  },
  textInput: {
    textAlign: "center",
    padding: 10,
    width: "80%",
    height: 50,
    margin: 5,
    borderRadius: 30,
    backgroundColor: "#fff",
  },
  button: {
    textAlign: "center",
    borderWidth: 2,
    borderColor: "gray",
    borderRadius: 30,
    padding: 10,
    margin: 5,
    width: "40%",
  },
});
