import { Text, View, TouchableOpacity } from "react-native";
import { styles } from "../../style/style";
import React from "react";
import { useState } from "react";
// service
import { NavigationService } from "../../service/NavigationService";
import { Users } from "../../models/users";
import { StorageData } from "../../service/StorageDataService";
import { Auth } from "../../models/auth";
// models

// localstorage
const USERS_INFO = "@userInfo";
const AUTH_INFO = "@authInfo";

export default function Menu({ navigation }) {
  const navigationService = new NavigationService();
  const storageData = new StorageData();

  const [users, setUsers] = useState([]);
  const [user, setUser] = useState(new Users());
  const [auth, setAuth] = useState([]);
  const [authValidate, setAuthValidate] = useState(new Auth());

  const getUser = async () => {
    const dataUser = await storageData.getDataStorage(USERS_INFO, users);
    const authUser = await storageData.getDataStorage(AUTH_INFO, auth);
    if (dataUser && authUser) {
      setUsers(dataUser);
      setAuth(authUser);
      if (users && auth) {
        users.map((user) => setUser(user));
        setAuthValidate({ auth: true });
      }
    }
  };

  if ((users.length !== 0 && auth.length !== 0) || !authValidate) {
    return (
      <View style={styles.container}>
        <Text style={styles.title}>Bienvenido</Text>
        <Text style={styles.text}>Selecciona una opci√≥n</Text>
        <TouchableOpacity
          style={styles.button}
          onPress={() => {
            navigationService.navigateProductsForm({ navigation });
          }}
        >
          <Text>Products</Text>
        </TouchableOpacity>
        <TouchableOpacity
          style={styles.button}
          onPress={() => {
            navigationService.navigateSaleForm({ navigation });
          }}
        >
          <Text>Sale</Text>
        </TouchableOpacity>
        <TouchableOpacity
          style={styles.button}
          onPress={() => {
            navigationService.navigateUsersList({ navigation });
          }}
        >
          <Text>Users</Text>
        </TouchableOpacity>
        <TouchableOpacity
          style={styles.subtitle}
          onPress={() => {
            navigationService.logout({ navigation });
          }}
        >
          <Text>Cerrar Sesion</Text>
        </TouchableOpacity>
      </View>
    );
  } else {
    getUser();
  }
}
