import { Text, View, TouchableOpacity } from "react-native";
import { styles } from "../../style/style";
import React from "react";
import { Users } from "../../models/users";
import AsyncStorage from "@react-native-async-storage/async-storage";
import { NavigationService } from "../../service/NavigationService";

const USER_INFO = "@userInfo";

export default function Menu({ navigation }) {
  let user = new Users();

  const navigationService = new NavigationService();

  const getStorageData = async () => {
    try {
      user = await AsyncStorage.getItem(USER_INFO);
    } catch (e) {
      await AsyncStorage.clear();
    }
  };
  return (
    <View style={styles.container}>
      <Text style={styles.title}>Bienvenido</Text>
      <Text style={styles.subtitle}>{user.name}</Text>
      <Text style={styles.text}>Selecciona una opci√≥n</Text>
      <TouchableOpacity
        style={styles.button}
        onPress={() => {
          navigationService.navigateProductsForm({ navigation });
        }}
      >
        <Text>Products</Text>
      </TouchableOpacity>
      <TouchableOpacity
        style={styles.button}
        onPress={() => {
          navigationService.navigateSaleForm({ navigation });
        }}
      >
        <Text>Sale</Text>
      </TouchableOpacity>
      <TouchableOpacity
        style={styles.button}
        onPress={() => {
          navigationService.navigateUsersList({ navigation });
        }}
      >
        <Text>Users</Text>
      </TouchableOpacity>
      <TouchableOpacity
        style={styles.text}
        onPress={() => {
          navigationService.logout({ navigation });
        }}
      >
        <Text>Cerrar Sesion</Text>
      </TouchableOpacity>
    </View>
  );
}
