import { Text, View, TouchableOpacity } from "react-native";
import { styles } from "../../style/style";
import React from "react";
import { useState } from "react";
// service
import { NavigationService } from "../../service/NavigationService";
import { Users } from "../../models/users";
import { StorageData } from "../../service/StorageDataService";
import { Auth } from "../../models/auth";
// models

// localstorage
const USERS_INFO = "@userInfo";
const AUTH_INFO = "@authInfo";

export default function Menu({ navigation, route }) {
  const navigationService = new NavigationService();
  const storageData = new StorageData();

  let user = new Users();
  let users = [];

  const getUsersStorage = async () => {
    await storageData
      .getDataStorage(USERS_INFO, users)
      .then((response) => {
        if (response) {
          users = JSON.parse(response);
          if (users) {
            user = users[0];
          }
        } else {
          navigationService.logout({ navigation });
        }
      })
      .catch((e) => console.log(e));
  };
  getUsersStorage();

  return (
    <View style={styles.container}>
      <Text style={styles.title}>Bienvenido</Text>
      <Text style={styles.text}>Selecciona una opci√≥n</Text>
      <TouchableOpacity
        style={styles.button}
        onPress={() => {
          navigationService.navigateProductsForm({ navigation });
        }}
      >
        <Text>Products</Text>
      </TouchableOpacity>
      <TouchableOpacity
        style={styles.button}
        onPress={() => {
          navigationService.navigateSaleForm({ navigation });
        }}
      >
        <Text>Sale</Text>
      </TouchableOpacity>
      <TouchableOpacity
        style={styles.button}
        onPress={() => {
          navigationService.navigateUsersList({ navigation });
        }}
      >
        <Text>Users</Text>
      </TouchableOpacity>
      <TouchableOpacity
        style={styles.subtitle}
        onPress={() => {
          navigationService.logout({ navigation });
        }}
      >
        <Text>Cerrar Sesion</Text>
      </TouchableOpacity>
    </View>
  );
}
