import React from "react";
import {
  StyleSheet,
  Text,
  View,
  TextInput,
  TouchableOpacity,
} from "react-native";
import { styles } from "../style/style";
import axios from "axios";
import { URL } from "../environments/env";
import { Users } from "../models/users";

export default function SignIn({ navigation }) {
  //#region atributos
  let users = [];
  let user = new Users();
  let auth = false;
  //#endregion

  //#region functions

  //#region services
  //get all users in the collection
  async function getUsers() {
    await axios
      .get(`${URL}/users/getAll`)
      .then((response) => {
        if (response.data) {
          users = response.data;
        }
        console.log(users);
      })
      .catch((e) => {
        // Podemos mostrar los errores en la consola
        console.log(e);
      });
  }
  //get one user in the collection
  async function getById(id) {
    await axios
      .get(`${URL}/users/getById/${id}`)
      .then((response) => {
        if (response.data) {
          user = response.data;
          if (user) {
            console.log(user);
            return user;
          }
        }
      })
      .catch((e) => {
        console.log(e);
      });
  }
  //get one by email for login
  async function getByEmail(email) {
    await axios
      .get(`${URL}/users/getByEmail/${email}`)
      .then((response) => {
        if (response.data) {
          user = response.data;
          return user;
        }
      })
      .catch((e) => {
        console.log(e);
      });
  }
  //#endregion

  //#region events
  const [email, emailText] = React.useState("");
  //#endregion

  //#endregion

  return (
    <View style={styles.container}>
      <Text style={styles.title}>Sign In</Text>
      <Text style={styles.subtitle}>Login to the application</Text>
      <TextInput
        style={styles.textInput}
        placeholder="Email"
        value={user.email}
        onChange={(e) => {
          emailText = e.target;
        }}
      />
      <TextInput
        style={styles.textInput}
        placeholder="Password"
        value={user.password}
      />
      <TouchableOpacity
        style={styles.button}
        onPress={() => {
          const userFind = getByEmail(email);
          if (userFind) {
            auth = true;
            navigation.navigate("Products", {
              user: userFind,
              auth: auth,
            });
          }
        }}
      >
        <Text>Sign In</Text>
      </TouchableOpacity>
      <TouchableOpacity
        onPress={() => {
          navigation.navigate("Signup");
        }}
      >
        <Text style={styles.text}>Don't have an account?</Text>
      </TouchableOpacity>
    </View>
  );
}
