import AsyncStorage from "@react-native-async-storage/async-storage";
import { Users } from "../models/users";
import { Products } from "../models/products";
import { Sale } from "../models/sale";

const USER_INFO = "@userInfo";
const AUTH_INFO = "@authInfo";
const PRODUCT_INFO = "@productInfo";
const SALE_INFO = "@saleInfo";

// let user = new Users();
// let product = new Products();
// let sale = new Sale();
// let auth = false;

const userAuthInfo = async () => {
  try {
    this.user = await AsyncStorage.getItem(USER_INFO);
    this.auth = await AsyncStorage.getItem(AUTH_INFO);
    this.product = await AsyncStorage.getItem(PRODUCT_INFO);
    this.sale = await AsyncStorage.getItem(SALE_INFO);
    if (user && auth && product && sale) {
      return user, auth, product, sale;
    }
  } catch (e) {
    console.log(e);
    AsyncStorage.clear();
  }
};
export { userAuthInfo };
