import React from "react";
import {
  StyleSheet,
  Text,
  View,
  TextInput,
  TouchableOpacity,
} from "react-native";
import { styles } from "../style/style";
import axios from "axios";
import { URL } from "../environments/env";
import { Users } from "../models/users";

export default function SignIn({ navigation }) {
  //#region atributos
  let users = [];
  let user = new Users();

  //#endregion

  //#region functions

  //#region services

  async function getUsers() {
    await axios
      .get(`${URL}/users/getAll`)
      .then((response) => {
        if (response.data) {
          users = response.data;
        }
        console.log(users);
      })
      .catch((e) => {
        // Podemos mostrar los errores en la consola
        console.log(e);
      });
  }

  async function getById(id) {
    await axios
      .get(`${URL}/users/getById/${id.toString()}`)
      .then((response) => {
        if (response.data) {
          user = response.data;
        }
        console.log(user);
      })
      .catch((e) => {
        console.log(e);
      });
  }

  //#endregion

  //#endregion

  return (
    <View style={styles.container}>
      <Text style={styles.title}>Sign In</Text>
      <Text style={styles.subtitle}>Login to the application</Text>
      {/* <TextInput style={styles.textInput} placeholder="Email" /> */}
      <TextInput style={styles.textInput} placeholder="Username" />
      <TextInput style={styles.textInput} placeholder="Password" />
      <TouchableOpacity
        style={styles.button}
        onPress={() => {
          // navigation.navigate("Products");
          const usersLoaded = getUsers();
          if (usersLoaded) {
            console.log(usersLoaded);
          } else {
            console.log(usersLoaded);
          }
        }}
      >
        <Text>Sign In</Text>
      </TouchableOpacity>
      <TouchableOpacity
        onPress={() => {
          // navigation.navigate("Signup");
          const userLoaded = getById("64764b66d958050e406d0c61");
          if (userLoaded) {
            console.log(userLoaded);
          } else {
            console.log(userLoaded);
          }
        }}
      >
        <Text style={styles.text}>Don't have an account?</Text>
      </TouchableOpacity>
    </View>
  );
}
