import React from "react";
import {
  StyleSheet,
  Text,
  View,
  TextInput,
  TouchableOpacity,
  FlatList,
} from "react-native";
import { styles } from "../style/style";

export default class SignUp extends React.Component(navigation) {
  constructor(props) {
    super(props);
    this.state = {
      loading: false,
      pokemon: [],
      url: "https://pokeapi.co/api/v2/pokemon/",
    };
  }

  getUsers() {
    this.setState({ loading: true });

    fetch(this.state.url)
      .then(res.json())
      .then((res) => {
        this.setState({
          pokemon: res.results,
          url: res.next,
          loading: false,
        });
      })
      .catch((err) => console.log(err));
  }
  render() {
    if (this.state.loading) {
      return (
        <View style={styles.container}>
          <Text style={styles.title}>Loading users...</Text>
        </View>
      );
    } else {
      <View style={styles.container}>
        <Text style={styles.title}>Pokemons</Text>
        <FlatList
          data={this.state.pokemon}
          renderItem={({ item }) => (
            <Text style={styles.title}>{item.name}</Text>
          )}
        />
      </View>;
    }
    // return (
    //   <View style={styles.container}>
