    //     <TextInput style={styles.textInput} placeholder="Email" />
    //     <TextInput style={styles.textInput} placeholder="Password" />
    //     <TouchableOpacity
    //       style={styles.button}
    //       onPress={() => {
    //         navigation.navigate("Signin");
    //       }}
    //     >
    //       <Text>Sign Up</Text>
    //     </TouchableOpacity>
    //     <TouchableOpacity
    //       onPress={() => {
    //         navigation.navigate("Signin");
    //       }}
    //     >
    //       <Text style={styles.text}>Already have an account?</Text>
    //     </TouchableOpacity>
    //   </View>
    // );
  }
}
